#include <bits/stdc++.h>
using namespace std;

int main() {

    int n;
    cin >> n;
    if (n == 0) {
        cout << 6;
        return 0;
    }
    int x = int(log10(n)) + 1;
    int cnt = 0;

    for (int i = 0; i < x; i++) {
        int k = n % 10;
        n /= 10;
        if (k == 0 || k == 6 ||k == 9)
            cnt += 6;
        if (k == 1)
            cnt += 2;
        if (k == 3 ||k ==5 || k==2)
            cnt += 5;
        if (k == 4)
            cnt += 4;
        if (k == 7)
            cnt += 3;
        if (k == 8)
            cnt += 7;

    }
    cout << cnt;
    
}
