#define int long long
using namespace std;

int32_t main() {
    // n >= 7, a(n) = (n^3-16*n+27)/6
    int n;
    cin >> n;
    if (n <= 3)
        return cout << 1, 0;
    if (n == 4)
        return cout << 3, 0;
    if (n == 5)
        return cout << 8, 0;
    if (n == 6)
        return cout << 21, 0;
    int ans;
    if (n % 2 == 0)
        ans = (n * n * n - 16 * n + 30) / 6;
    else
        ans = (n * n * n - 16 * n + 27) / 6;
    cout << ans;

}
