def solve(arr, n):
	
	arr.append('1')
	
	goo, d = [], 0
	
	for i in range(n):
		if arr[i] == arr[i+1]:
			d += 1
		else:
			goo.append(d+1)
			d = 0
			
	goo.sort()
			
	if len(set(goo)) == 1:
		return 'YES'

	if len(set(goo)) == 2:
		if goo[-1] - goo[-2] == 1:
				return 'YES'
		if goo[0] != goo[1] and goo[0] == 1:
			return 'YES'
	return 'NO'

foo = list(input())
foo.sort()

print(solve(foo, len(foo)))
