#include<bits/stdc++.h>
#define INF 200002
#define ll long long
ll dp[INF], arr[INF], xr;

using namespace std;
int main()
{
    ios_base::sync_with_stdio(0);
    cin.tie(NULL);
    cout.tie(NULL);
    ll n, m;
    cin >> n;
    for (ll i = 1; i <= n; i++)
    {
        cin >> arr[i];
    }

    for (ll i = 0; i < n; i++)
    {
        xr = xr ^ arr[n-i];
    }

    arr[++n] = xr;
    xr = 0;

    for (ll i = 1; i <= n; i++)
    {
        xr = xr ^ arr[i];
        dp[i] = xr;
    }

    ll q, l, r, z, x;
    cin >> q;

    while (q--)
    {
        cin >> l >> r;
        if (r <= n)
        {
            z = dp[r];
            x = dp[l - 1];
            cout << (z ^ x) << endl;
        }
        else
        {
            l = (l - 1) % n + 1;
            r = (r - 1) % n + 1;
            if (l <= r)
            {
                z = dp[r];
                x = dp[l - 1];
                cout << (z ^ x) << endl;
            }
            else
            {
                z = dp[n];
                x = dp[l - 1];
                cout << ((z ^ x) ^ dp[r]) << endl;
            }
        }
    }


}
