#include <iostream>
#include<vector>
#include<algorithm>

using namespace std;
#define ll long long
const ll MAXSIZE = 1e6+2;
ll C[MAXSIZE] = {0};

ll binpow(ll a, ll p){
    ll  MOD = 1e9+7;
    ll res = 1;
    while(p){
        if(p&1){
            res *= a;
            res %= MOD;
            p--;
        }
        a *= a;
        a %= MOD;
        p >>= 1;
    }

    return res;
}

void precalc(ll k){
    ll  MOD = 1e9+7;
    int i = k;
    C[i++] = 1;
    for(;i < MAXSIZE; i++){
        C[i] = C[i-1]*i%MOD*binpow(i-k,MOD-2)%MOD;
    }
}
int main()
{

    ll n,k;
    cin >> n >> k;
    precalc(k - 1);
    vector<ll>mp;
    for(int i=0;i<n;i++){
        ll a;
        cin >> a;
        mp.push_back(a);
    }
    sort(mp.begin(),mp.end());
    ll p = 1e9+7,s=0;
    for(int i = k - 1; i < n; i ++){
        s = (s + 1LL * mp[i] * C[i] % p) % p;
    }
    cout << s;
    return 0;
}
