#include <iostream>
using namespace std;

int solve(int N, int K)
{
    int sum = 0;
    while (N != 0)
    {
        sum += (N % 10);
        N /= 10;
    }
    if (sum == K) return true;
    return false;
}

int main()
{
    int L, R, N;
    cin >> L >> R >> N;

    while (L <= R)
    {
        if (solve(L, N))
            {
                cout << L << endl;
                break;
            }
            L++;
    }
    while (L <= R)
    {
        if (solve(R, N))
            {
                cout << R << endl;
                break;
            }
            R--;
    }

    return 0;
}
