#include<bits/stdc++.h>
using namespace std;

double solve(double m, double n)
{
  double a, s = 0.0;
  
  for (int i = 1; i < n; i++)
  {
    a = (double)(sqrt(3) * (double)(m / 2));
    s = (double)(s + a * (double)(m / 4));
    m = a;
  }
  
  return s;
}

int main()
{
  double m, n;
  cin >> m >> n;
  
  if (n > 12) n = 13;
  
  printf("%.2f",solve(m, n));
  
  return 0;
}
