#include<bits/stdc++.h>
using namespace std;

double solve(double m, double n, double y, double x)
{
  double a, s = 0.0;
  double b, c = 0.0;
  
  while (n-1!=0&&x-1!=0)
  {
    a=(double)(sqrt(3)*(double)(m/2));
    b=(double)(sqrt(3)*(double)(y/2));
    s=(double)(s+b*(double)(y/4));
    n-=1; x-=1; m=a; y=b;
  }
  
  if (n==1&&x==1) return s;
  if (n==1)
  {
    for (int i = 1; i < x; i++)
    {
    	b=(double)(sqrt(3)*(double)(y/2));
    	s=(double)(s+b*(double)(y/4));
    	y=b;
    }
    return s;
  }
  if (x==1)
  {
    for (int i = 1; i < n; i++)
    {
      a=(double)(sqrt(3)*(double)(m/2));
      s=(double)(s+a*(double)(m/4));
      m=a;
    }
    return s;
  }
}

int main()
{
  double m, n, y, x;
  cin >> m >> n >> y >> x;
  
  if (n > 12) n = 13;
  if (x > 12) x = 13;
  
  printf("%.2f",solve(m, n, y, x));
  
  return 0;
}
