#include <bits/stdc++.h>
using namespace std;

void printMatrix(vector<vector<int> >& mat, int N, int M)
{

	for (int i = 0; i < N; i++) {

		for (int j = 0; j < M; j++) {

			printf("%d ",mat[i][j]);
		}
		printf("\n");
	}
}

void MatNoAdjacentElemEq(vector<vector<int> >& mat, int N, int M)
{
	for (int i = 0; i < N; i++) {

		for (int j = 0; j < M; j++) {

			if (i % 2 == 0) {

				if (j % 2 == 0 && mat[i][j] % 2 != 0) {

					mat[i][j]++;
				}

				else if (j % 2 != 0 && mat[i][j] % 2 == 0) {

					mat[i][j]++;
				}
			}

			else {

				if (j % 2 == 0 && mat[i][j] % 2 == 0) {

					mat[i][j]++;
				}

				else if (j % 2 != 0 && mat[i][j] % 2 != 0) {

					mat[i][j]++;
				}
			}
		}
	}

	printMatrix(mat, N, M);
}

int main()
{
    int N, M, value;
    scanf("%d%d", &N, &M);

	vector<vector<int> > mat;

	for(int i = 0; i < N; ++i) {

		vector<int> row;

		for(int j = 0; j < M; ++j){

			scanf("%d", &value);
			row.push_back(value);

		}

		mat.push_back(row);
	}
	MatNoAdjacentElemEq(mat, N, M);

	return 0;
}
