def solve(n, m, k):
    arr = list(map(int,input().split()))
    b = list()
    for i in arr:
        if m >= i:
            b.append(i)
    if len(b) < k: return "No"
    ans = 0
    b.sort()
    n = len(b)
    i = 0
    while i + 1 < n:
        if b[i+1] - b[i] > 1:
            ans += 1
            i += 1
        else:
            i += 2  
    if len(b) - ans >= k: return "Yes"
    if ans != 0:
        x = len(b) - ans
        if x % 2 == 0 and x + 1 >= k:
            return "Yes"
        if x >= k:
            return "Yes"
    return "No"
            
    
n, m, k = map(int,input().split())
print(solve(n, m, k))
