#include<iostream>
#include<vector>
#include<list>
#include<map>
using namespace std;
int solve()
{
	int n, k;
	scanf("%d%d",&n,&k);
	list<int>v,lt,lt2;
	v.push_back(0);
	if (k == 1) return 1;
	for (int i=1;i<=n;i++) {
		v.push_back(i);
	}
	int key = 1;
	int ans = 0;
	while (key) {
		ans++;
		v.push_back(v.front());
		v.pop_front();
		lt = v;
		int y = 1;
		while (lt.size()>1) {
			while (!lt.empty()) {
				if (y % k != 0) {
					lt2.push_back(lt.front());
					lt.pop_front();
					y++;
				}
				else {
					lt.pop_front();
					y++;
				}
			}
			y = (y - 1) % k + 1;
			lt.swap(lt2);
		}
		key = lt.front();
		lt.pop_front();
	}
	return ans;
}
int main()
{
	int t = 1;
	//scanf("%d",&t);
	while (t--)
	{
	 	cout << solve() << endl;
	}
	return 0;
}
