#include <bits/stdc++.h>
using namespace std;
const int MAX = 101;
int arr[MAX] = {0};
int main()
{
    int n, m;
    cin >> n;
    
    for (int i = 0; i < n; i++) {
      cin >> m;
      arr[m]++;
    }
    n = 101;
    int s = 0;
    while (n--) {
      s += (arr[n] / 2);
    }
    
    cout << s;

    return 0;
}
