#include <bits/stdc++.h>
using namespace std;

int main()
{
    int n;
    cin >> n;
    
    int arr[n];
    for (int i = 0; i < n; i++) {
      cin >> arr[i];
    }
    
    sort(arr, arr + n);
    
    int mx = 1, l = 1;
    for (int i = 1; i < n; i++) {
      if (arr[i-1] == arr[i]) {
        l++;
      }
      else {
        mx = max(mx, l);
        l = 1;
      }
    }
    
    cout << max(mx, l);
    
  return 0;
}
