#include <iostream>
#include <map>
using namespace std;

void solve() {
	
	int n, m, q;
	scanf("%d%d", &n, &q);
	map < int, int > mp;
	while (n--) {
		scanf("%d", &m);
		mp[m] = 1;
	}
	
	while (q--)
	{
		scanf("%d", &m);
		if (mp.find(m) == mp.end()) {
			printf("NO\n");
		}
		else {
			printf("YES\n");
		}
	}
	
	
}

int main()
{
	int t = 1;
	while (t--) solve();
	return 0;
}
