#include <bits/stdc++.h>
using namespace std;
int const N=1e5+10;
int a[N];
int test(int len, int k) {
    int mini = 0;
    int maxi = 0;
    int boshi = 0;
    int oxiri = 0;
    int maxL = 0;
    for (int i = 1; i < len; i++) {
        oxiri = i;
        if (a[i] < a[mini])
            mini = i;
        if (a[i] > a[maxi])
            maxi = i;
        if (a[maxi] - a[mini] > k) {
            if (oxiri - boshi > maxL)
                maxL = oxiri - boshi;

            boshi++;
            maxi = mini = boshi;
            i = boshi;
        }
    }
    return maxL;
}
int main(){
ios::sync_with_stdio(false);
cin.tie(0);cout.tie(0);
    int n, k; cin >> n >> k;
    for(int i = 0; i < n; i++) cin >> a[i];
    cout << test(n, k);
    return 0;
}
