#include <bits/stdc++.h>
using namespace std;
void solve(int s, int k) {
  int n;
  scanf("%d", &n);
  while (n--) {
    s = s + k / 2;
    k = int(k / 2) * 3;
  }
  printf("%d", s);
}
int main()
{
    int t = 1;
    
    while (t--) {
      solve(0, 5);
    }
    
    return 0;
}
