#include<bits/stdc++.h>
using namespace std;

int solve(double arr[], int l, int r, int x){
  if (r >= l)
  {
    int m = l + (r - l) / 2;
    if (arr[m] >= x) {
      return solve(arr, l, m - 1, x);
    }
    if (arr[m+1] >= x) {
      return m + 1;
    }
    return solve(arr, m + 1, r, x);
  }
}

int main()
{
   int n;
   scanf("%d", &n);
   int arr[n];
   for (int i = 0; i < n; i++) {
     scanf("%d", &arr[i]); 
   }
   sort(arr, arr + n);
   double p[n];
   double sm = 0;
   for (int i = 0; i < n; i++) {
     sm += arr[i];
     p[i] = (double)(sm / (i + 1));
   }
   int t;
   scanf("%d", &t);
   while (t--) {
     int x;
     scanf("%d", &x);
     if (p[0] >= x) {
       printf("%d\n", 0);
     }
     else
     if (p[n - 1] < x) {
       printf("%d\n", n);
     }
     else
     printf("%d\n", solve(p, 0, n - 1, x));
   }
   
   return 0;
}

