#include<bits/stdc++.h> 
#define int long long
using namespace std; 

void findNum(int n, int k) 
{ 
	
	int q = k / (n - 1); 
	int r = k % (n - 1); 
	int a; 

	if(r != 0) 
	a = (n * q) + r; 
	else
	a = (n * q) - 1; 

	cout << a; 
} 

int32_t main() 
{ 
	int A, N;
	cin >> N >> A;
	
	findNum(A, N); 
	return 0; 
} 

