def solve(n):
    
    if n == 1: return 1
    mod = 11000000077
    
    if n % 2:
        a = (pow(12, n // 2 + 1, mod) - 1) // 11
        b = (3 * (pow(12, n // 2, mod) - 1)) // 11
        return (a + b) % (mod // 11)
    a = (pow(12, n // 2, mod) - 1) // 11
    b = (3 * (pow(12, n // 2, mod) - 1)) // 11
    return (a + b) % (mod // 11)
    
n = int(input())
print(solve(n))
