def solve1(arr):

	n = len(arr)

	for i in range(n):
		arr[i] = chr(219-ord(arr[i]))
	
	return ''.join(arr)

def solve(cur):

	size = len(cur)
	
	if size % 2: 
		size -= 1

	for i in range(1, size, 2):
		cur[i-1],cur[i]=cur[i],cur[i-1]

	return solve1(cur);

print(solve(list(input())))
