#include <bits/stdc++.h>
using namespace std;

const int MAX = 2000002;
int arr[MAX] = {false};

int solve() {

    int n, k, m;
    scanf("%d%d", &n, &k);

    for (int i = 0; i < n; i++) {
        cin >> m;
        arr[m] = true;
    }
    for (int i = 0; i < MAX; i++) {
        if (!arr[i]) {
            k--;
            if (k == -1) {
                return i;
            }
        }
    }

}

int main()
{

    int t = 1;

    while (t--) {
        cout << solve();
    }

    return 0;
}
