def solve(cur, k):

	ans = ''
	for i in cur:

		if i.isupper():
			x = ord(i) - 64
			if x + k <= 26:
				ans += chr(x + k + 64)
			else:
				ans += chr(x + k + 38)
		
		elif i.islower():
			x = ord(i) - 96
			if x + k <= 26:
				ans += chr(x + k + 96)
			else:
				ans += chr(x + k + 70)
		
		else: ans += i
	return ans

k = int(input())
print(solve(input(), k % 26))
