#include <bits/stdc++.h>
using namespace std;
int solve() {
    int k, a, l, r;
    cin >> a >> k >> l >> r;

    int n = l / a;
    if (n * a + k < l)
        n++;

    l = (n * a + k);//5 1 3 8

    int m = r / a;
    if (m * a + k > r)
        m--;
    r = (m * a + k);

    return ((r - l) / a) + 1;


}
int main()
{
    int t = 1;
    //scanf("%d", &t);
    while (t--) {
        cout << solve() << endl;
    }
    return 0;
}
