def function(x,y):
	n, k = map(int,input().split())
	n -= 1
	if k == 0:
		return 1
	if k == n:
		return 0
	while k != n and x != k:
		n -= 1
		x += 1
		y += 2
	if y == 0:
		if k == n:
			return 0
		else:
			return 1
	else:
		if k == n:
			return y
		else:
			return y + 1
