from sys import *

def binarySearch(l, r, k):
  if r >= l:
    mid = l + (r - l) // 2
    print('?', mid)
    stdout.flush()
    x = int(input())

    if r - l < 2 and x == 0:
      print('!', mid)
      return;
    elif x == 0:
    	return binarySearch(l, r-1, mid)
    
    if mid > x:
      return binarySearch(l, mid-1, k)
    else:
      return binarySearch(mid+1, r, k)
  print('!', k)

binarySearch(1, 10**9, 0)
