#include<bits/stdc++.h>
using namespace std;

int solve() {
    int n;
    cin >> n;

    string st;
    cin >> st;

    int var = 0;
    int ans = 0;
    int cur = 0;

    for (int i = 0; i < n; i++) {
            cur = var;
        if (st[i] == 'D') {
            var--;
        }
        else {
            var++;
        }
        if (cur < 0 && var == 0) {
            ans++;
        }
    }

    return ans;
}

int main()
{

    int t = 1;

    while (t--)
        cout << solve() << endl;


    return 0;
}
