def solve(n, k):
	
	arr1 = []
	arr2 = []

	for i in range(n):
		x, y = map(int,input().split())
		if y == 0:
			arr1.append(x)
		else:
			arr2.append(x)

	sm = sum(arr1)
	arr2.sort()
	s1, s2 = 0, 0
	if k <= len(arr2):
		for i in range(k):
			s1 += arr2[-i-1]
		for i in range(len(arr2) - k):
			s2 += arr2[i]

		return sm + s1 - s2;
	return sm + sum(arr2)

n, k = map(int,input().split())
print(solve(n, k))
