#include<bits/stdc++.h>
using namespace std;

const int MAX = 101;
int arry[MAX] = {0};

int solve() {
    int n;
    cin >> n;
    int arr[n];
    for (int i = 0; i < n; i++) {
        cin >> arr[i];
        arry[arr[i]]++;
    }

    int mx = -1;

    for (int i = 0; i < 101; i++) {
        if (arry[i] > mx) {
            mx = arry[i];
        }
    }

    return n - mx;

}

int main()
{

    int t = 1;
    //cin >> t;

    while (t--)
        cout << solve() << endl;


    return 0;
}
