#include <bits/stdc++.h>
using namespace std;
string solve() {
    int n;
    scanf("%d", &n);

    int k = n % 3;
    n -= k;

    while (k % 5 != 0 && n >= 0) {
        k += 3;
        n -= 3;
    }

    string curr = "";
    if (k % 5 != 0 || n < 0)
        return "-1";
    for (int i = 0; i < n; i++) {
        curr += "5";
    }
    for (int i = 0; i < k; i++) {
        curr += "3";
    }

    return curr;


}
int main()
{
    int t;
    scanf("%d", &t);
    while (t--) {
        cout << solve() << endl;
    }
    return 0;
}
