#include<bits/stdc++.h>
using namespace std;

int main()
{
   int n, m, i = 0;
   scanf("%d", &n);
   
   int arr[n];
   
   while (i < n) {
       scanf("%d", &arr[i]);
       i += 1;
   }
   
   sort(arr, arr + n);
   
   i = 0;
   int curr = 1;
   
   while (i < n) {
       int x = arr[i];
       while (arr[i+1] - x < 4 && i+1 < n) {
            i += 1;
       }
       if (i + 1 < n) curr += 1;
       i += 1;
   }
   
   cout << curr;
   
   return 0;
}
