#include <bits/stdc++.h>
using namespace std;

const int MAX = 1000001;
int arr[MAX] = {0};

int solve() {
    int n, m;
    cin >> n;

    for (int i = 0; i < n; i++) {
        cin >> m;
        arr[m] += 1;
    }

    int key = 0, ans = 0;

    for (int i = 0; i < n; i++) {
        cin >> m;
        if (arr[m] == 0) {
            key = 1;
        }
        else {
            ans++;
            arr[m] -= 1;
        }
    }

    if (key)
        for (int i = 0; i < MAX; i++) {
            if (arr[i] != 0) {
                return ans + 1;
            }
        }
    return ans - 1;

}

int main()
{
    int t = 1;
    while (t--) {
        cout << solve() << endl;
    }

    return 0;
}
