#include<bits/stdc++.h>
using namespace std;

string solve() {
    int n, k;
    scanf("%d%d", &n,&k);
    int arr1[n], arr2[n];

    for (int i = 0; i < n; i++) {
        scanf("%d", &arr1[i]);
    }

    for (int i = 0; i < n; i++) {
        scanf("%d", &arr2[i]);
    }

    sort(arr1, arr1 + n);
    sort(arr2, arr2 + n);
    reverse(arr2, arr2 + n);

    for (int i = 0; i < n; i++) {
        if (arr1[i] + arr2[i] < k) {
            return "NO";
        }
    }
    return "YES";

}

int main() {
    int t;
    scanf("%d", &t);

    while (t--) {
        cout << solve() << "\n";
    }
}
