#include <bits/stdc++.h>
using namespace std;

void printsolve(int arr[], int cur[], int n) {

    for (int i = 0; i < n; i++) {
            int mn = 100000007, in;
            for (int j = 0; j < n; j++) {
                if (arr[j] != -1 && arr[j] < mn) {
                    mn = arr[j];
                    in = j;
                }
            }
            arr[in] = -1;
            cout << cur[in] << ' ';
            //9 6 11 4 7
    }

}

int main()
{
    int n, x, y;
    cin >> n;

    int arr[n], cur[n];

    for (int i = 0; i < n; i++) {
        cin >> x >> y;
        arr[i] = x + y;
        cur[i] = i + 1;
    }

    printsolve(arr, cur, n);

    return 0;
}
