#include <bits/stdc++.h>

using namespace std;

int main()

{

    int n, k;

    cin >> n >> k;

    

    int arr[n];

    for (int i = 0; i < n; i++) {

      cin >> arr[i];

    }

    sort(arr,arr+n);

    list<int>lt;

    int ans = 0, t, tt;

    for (int i = 0; i < n; i++) {

      lt.push_back(arr[i]);

    }

    

    int key = 0;

    

    while (!lt.empty())

    {

      key = 0;

      t = lt.front() + k;

      while (lt.front() <= t && !lt.empty()) {

        tt = lt.front();

        lt.pop_front();

        key = 1;

      }

      t = tt;

      while (lt.front()-t<=k && !lt.empty()) {

        lt.pop_front();

        key = 1;

      }

      if (key == 0) lt.pop_front();

      ans++;

    }

    

    cout << ans;

    return 0;

}
