#include <bits/stdc++.h>
using namespace std;
int main()
{

    string a;
    cin >> a;

    cout << a.size() << endl;

    for (auto x : a) {
        int k = int(x);
        string s = "";
        while (k > 0) {
            if (k % 2 == 0) s += "0";
            else s += "1";
            k /= 2;
        }
        reverse(s.begin(), s.end());
        cout << s << endl;
    }





    return 0;
}
