#include <bits/stdc++.h>
using namespace std;

string solve() {
    int n;
    cin >> n;

    int arr[n];
    for (int i = 0; i < n; i++) {
        cin >> arr[i];
    }

    for (int i = 1; i <= n; i++) {
        if (arr[i - 1] != i) {
            return "NO";
        }
    }
    return "YES";
}

int main()
{
    int t = 1;
    while (t--) {
        cout << solve() << endl;
    }

    return 0;
}
