#include <bits/stdc++.h>
using namespace std;

const int MAX = 26;

int arr[MAX] = {0};
int ARR[MAX] = {0};

void solve() {
    string s;
    while (cin >> s) {
        for (int i = 0; i < s.length(); i++) {
            if ('a' <= s[i] && s[i] <= 'z') {
                arr[s[i] - 97]++;
            }
            else
            if ('A' <= s[i] && s[i] <= 'Z') {
                ARR[s[i] - 65]++;
            }
        }
    }
    for (int i = 0; i < MAX; i++) {
        cout << char(i + 97) << ' ' << arr[i] << '\n';
    }
    for (int i = 0; i < MAX; i++) {
        cout << char(i + 65) << ' ' << ARR[i] << '\n';
    }
    
}

int main()
{
    int t = 1;
    while (t--) {
        solve();
    }

    return 0;
}
