   while (i < n) {
       scanf("%d", &m);
       if (0 < m && m < n+1) {
           arr[m] = true;
       } i += 1;
   }
   
   i = 1;
   
   while (i < MAX) {
       if (arr[i] == false) {
           printf("%d", i);
           break;
       }i += 1;
   }
   
