#include <bits/stdc++.h>
using namespace std;

const int MAX = 101;
int arr[MAX] = {0};

int main()
{
	int n, k, t, s;
	cin >> n;
	while (n--) cin >> t, arr[t]++;
    cin >> k;
    while (k--){
        s = 0;
        cin >> t;
        for (int i = 0; i < t; i++)
            s += arr[i + 1];
        cout << s << '\n';
    }

	return 0;
}
