#include <bits/stdc++.h>
#define int long long
using namespace std;

int countFactor(int P, int X)
{
	if (X < P)
		return 0;

	return (X / P + countFactor(P, X / P));
}

int findSmallestX(int Y)
{
	int low = 0, high = 5 * Y;
	int N = 0;
	while (low <= high) {
		int mid = (high + low) / 2;

		if (countFactor(5, mid) < Y) {
			low = mid + 1;
		}

		else {
			N = mid;
			high = mid - 1;
		}
	}

	return N;
}

int32_t main()
{
	int t, n;
	cin >> t;

	while (t--){
        cin >> n;
        cout << findSmallestX(n) << endl;
	}
	return 0;
}
