#include <bits/stdc++.h> 
#define int long long
using namespace std; 

char binarySearch(int arr[], int l, int r, int x) 
{ 
	char A = 'A', B = 'B';
	
	if (r % 2) swap(A, B);
	
    while (l <= r) { 

        int m = l + (r - l) / 2; 


        if (arr[m] == x) 

            return m % 2 ? B : A; 


        if (arr[m] < x)
        {
          if (arr[m+1] >= x)
          {
            return (m+1) % 2 ? B : A;
          }
          l = m + 1;
        }

        else
		{
		  if (m == 0)
		    return A;
		  r = m - 1;
		}

    }

    //return -1; 
} 

  

int32_t main() { 

    int n;
    cin >> n;
    
    int arr[n];
    int sum = 0;
    
    for (int i = 0; i < n; i++)
    {
      cin >> arr[i];
    }
    
    for (int i = n-1; i >= 0; i--)
    {
      sum += arr[i];
      arr[i] = sum;
    }
    
    reverse(arr, arr + n);
    
    int Q, K;
    cin >> Q;
    
    while (Q--)
    {
      cin >> K;
      
      cout << binarySearch(arr, 0, n-1, K) << endl;
    }

    return 0; 
}
