#include<bits/stdc++.h>
#define ll long long
using namespace std;
ll solve(int n, int k, int p) {
	int arr[k];
	for (int i=0;i<k;i+=1) {
		scanf("%d",&arr[i]);
	}
	if (k+p>n) return -1;
	int ans = 0;
	for (int i=0;i<k;i+=1) {
		if (arr[i]<=p) p+=1;
		else return p;
	}
	return p;
}
int main()
{
	int t;
	scanf("%d",&t);
	while (t) {
		int n, k, p;
		scanf("%d%d%d",&n,&k,&p);
		printf("%lld\n",solve(n,k,p));
		t-=1;
	}
	return 0;
}
