#include <bits/stdc++.h>
#define int long long
using namespace std;
int solve(int a, int b){

    if(a == 0) return 0;
    if(2 * a <= b) return a;
    return (a+b)/3;
}
int32_t main() {
   int n, a, b;
   cin >> n;

   while(n--){
    cin >> a >> b;
    if (a > b) swap(a, b);
    cout << solve(a, b) << endl;
   }

   return 0;
}
