#include<bits/stdc++.h>
using namespace std;

int solve(int arr[],int n,int m)
{
    sort(arr, arr + n);
    int max = 0;
    int min = 0;
    for (int i = 0; i < n - m; i++)
    {
        max += arr[n - i - 1];
        min += arr[i];
    }
    return max - min;
}

int main()
{
    int t;
    cin >> t;

    while (t--)
    {
        int n, m;
        cin >> n >> m;
        int arr[n];
        for (int i = 0; i < n; i++)
        {
            cin >> arr[i];
        }
        cout << solve(arr, n, m) << endl;
    }

    return 0;
}
