#include <bits/stdc++.h>
using namespace std;

int solve(int n)
{
  int x;
  cin >> x;
  int min = x;
  int max = x;
  
  while (n--)
  {
    cin >> x;
    if (min > x) min = x;
    else
    if (max < x) max = x;
  }
  return max + min;
}

int main()
{
  int t, n;
  cin >> t;
  
  while (t--)
  {
    cin >> n;
    cout << solve(n - 1) << endl;
    
  }
  return 0;
}
