string solve(int arr[],int m,int p,int n)
{
  sort(arr, arr + n);
  int i = 0;
  while (i < n and m > 0 and p > 0)
  {
    m -= arr[i];
    p -= arr[i++];
  }
  if (m < 0 and p >= 0) return "Panda";
  if (m >= 0 and p < 0) return "Meshpolvon";
  
  return "Durang";
}

int main()
{
  int t, m, p, n, x;
  cin >> t;
  
  while (t--)
  {
    cin >> m >> p >> n;
    int arr[n], sum = 0;
    for (int i=0; i<n; i++) 
    {
      cin >> arr[i];
      sum += arr[i];
    }if (sum <= m and sum <= p or m == p)
    {
      cout << "Durang\n";
    }
    else
    if (sum <= m) 
    {
      cout << "Meshpolvon\n";
    }
    else
    if (sum <= p)
    {
      cout << "Panda\n";
    }
    else
    cout<<solve(arr, m, p, n)<<'\n';
  }
   
  return 0;
}
