#include <bits/stdc++.h>
using namespace std;

void printArr(int arr[], int n, int k)
{
    //2 4 1 3 5
	while (n > -1)// 2 4 1 3 5
    {
        int x = arr[n] % k;//0
        int y = n;//4 3 2 1
        while (x == arr[n-1]%k and n != 0)
        {
            n--;
        }
        for (int i = n; i <= y; i++)
        {
            cout << arr[i] << " ";
        }
        n -= 1;
    }
}

void sortWithRemainder(int arr[], int n, int k)
{

	vector<int> v[k];

	for (int i = 0; i < n; i++) {
		v[arr[i] % k].push_back(arr[i]);
	}

	for (int i = 0; i < k; i++)
		sort(v[i].begin(), v[i].end());

	int j = 0;
	for (int i = 0; i < k; i++) {

		for (vector<int>::iterator it = v[i].begin();
			it != v[i].end(); it++) {

			arr[j] = *it;
			j++;
		}
	}

	printArr(arr, n-1, k);
}

int main()
{
    int n, k;
    cin >> n >> k;

	int arr[n];
	for (int i = 0; i < n; i++)
        cin >> arr[i];

	sortWithRemainder(arr, n, k);

	return 0;
}
