#include <bits/stdc++.h>
using namespace std;

int solve(int arr[],int n)
{
    sort(arr, arr + n);
    int k = 2;

    for (int i = 0; i < n; i++)
    {
        if (k <= arr[i]) k += 2;
    }
    return k;
}

int main()
{
    int t;
    cin >> t;

    while (t--)
    {
        int n;
        cin >> n;
        int arr[n];

        for (int i = 0; i < n; i++)
            cin >> arr[i];
        cout << solve(arr, n) << '\n';
    }

	return 0;
}
