#include <bits/stdc++.h>
#define int long long

int gcd(int a, int b)
{
    if (b == 0) return a;
    return gcd(b, a % b);
}

using namespace std;

int32_t main()
{

    int n;
    cin >> n;

    int a = n - 2;
    while (gcd(a--,n)!=1);
    cout << a + 1;

    return 0;
}
