#include <bits/stdc++.h>
int const N = 1e5 + 9;
int f[N];
std::deque <int> a[10];
void push_right(int i) {
    for (;i > 0; i -= i & -i)
        f[i]++;
}
int find_current(int i) {
    int sum = 0;
    for (;i < N; i += i & -i) {
        sum += f[i];
    }
    return sum;
}

int solve() {
    memset(f, 0, sizeof f);
    int n, k;
    scanf("%d%d", &n, &k);
    for (int i = 1, x; i <= n; i++) {
        scanf("%d", &x);
        a[x].push_back(i);
    }
    for (int i = 1; i <= n; i++) {
        for (int d = 9; d >= 0; d--) {
            if (a[d].size() > 0) {
                int index = a[d][0];
                int current = index + find_current(index);
                int attempts = current - i;
                if (k >= attempts) {
                    printf("%d", d);
                    k -= attempts;
                    push_right(index);
                    a[d].pop_front();
                    break;
                }
            }
        }
    }
    return puts("") * 0;
}
int main()
{
    int t;
    scanf("%d", &t);

    while (t--)
        solve();

    return 0;
}
